mapper.select:
SELECT @rownum := @rownum + 1 AS no, Tb.*
FROM ( SELECT FILE_ID AS fileId ,file_Version AS fileVersion ,file_nm AS fileNm ,FILE_SIZE AS fileSize ,file_hash as fileHash ,file_download_path as fileDownloadPath ,DATE_FORMAT(fcft.REG_DATE,'%Y-%m-%d %T') AS regDate ,fcft.REG_ID AS regId ,fcft.MOD_ID AS modId ,DATE_FORMAT(fcft.MOD_DATE,'%Y-%m-%d %T') AS modDate ,sat.admin_nm AS adminNm ,ip_address FROM fim_client_file_tb fcft LEFT JOIN sim_admin_tb sat ON fcft.reg_id = sat.login_id INNER JOIN ( SELECT @rownum := 0) R
WHERE FILE_ID = #{paramMap.fileId}
ORDER BY file_Version desc, fcft.reg_date desc
) Tb
GROUP BY fileNm
